  // }


  // console.log(days);


  return (
    <Calendar>
      {/* <Header /> */}
      <Title />
      <Monitor today={today} />
      <Grid startDay={startDay}/>
      {/* <Footer /> */}
    </Calendar>
  );
}

export default App;
