import './App.css';
import moment from "moment";
import { Grid } from '../Calendar/Grid/Grid';
import { Calendar } from './AppStyle';
import { Monitor } from '../Calendar/Monitor/Monitor';
import { Title } from '../Calendar/Title/Title';

function App() {

  moment.updateLocale("ru", {week: {dow: 1}});
  moment.lang("ru");
  // moment.locale("ru");
  const today = moment();
  const startDay = today.startOf('month').startOf('week');
  // const endDay = moment().endOf('month').endOf('week');

  // let days = [];
  // let day = startDay.clone();

  // while(day.isBefore(endDay)) {
  //   days.push(day.clone());
  //   day.add(1, 'day');
  // }


  // console.log(days);


  return (
    <Calendar>
      {/* <Header /> */}
      <Title />
      <Monitor today={today} />
      <Grid startDay={startDay}/>
      {/* <Footer /> */}
    </Calendar>
  );
}

export default App;
