import styled from 'styled-components';

export const Cell = ((isWeekend) => {styled.div`
    min-width: 140px;
    min-height: 80px;
    background-color: ${isWeekend ? '#272829' : '#1e1f21'};
    color: #dddcdd;
`});

export const Row = styled.div`
    display: flex;
    justify-content: ${props => props.justifyContent ? props.justifyContent : 'flex-start'};
`;

export const Day = styled.div`
    height: 33px;
    width: 33px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 2px
`;

export const CurrentDay = styled(Day)`
    background: #f00;
    border-radius: 50%;
    height: 100%;
    width: 100%;
`
